import React, { useState, useEffect } from "react";
import { useTranslation } from "react-i18next";
import drapeau from "../images/drapeau_MLG.png";
import ScrollReveal from "scrollreveal";
import { Link } from "react-scroll";

function Header() {
  const [t, i18n] = useTranslation("global");
  const [elements, setElements] = useState([t("header.li1"), t("header.li2"), t("header.li3"), t("header.li4")]);
  const [elementSelectionner, setElementSelectionner] = useState(t("header.li1"));
  const [visibility, setVisibility] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const [ulDisplay, setUlDisplay] = useState(false);

  useEffect(() => {
    setElements([t("header.li1"), t("header.li2"), t("header.li3"), t("header.li4")]);
    setElementSelectionner(t("header.li1"));
  }, [i18n.language, t]);

  const handleSwitchLang = (lang) => {
    i18n.changeLanguage(lang);
  };

  const handleSwitchMgFr = () => {
    setVisibility(!visibility);
  };

  const handleClickListe = (element) => {
    setElementSelectionner(element);
  };

  const handleClickMenu = () => {
    setShowMenu(!showMenu);
    setUlDisplay(!ulDisplay);
  };

  const handleClose = () => {
    setUlDisplay(false);
  };

  useEffect(() => {
    ScrollReveal().reveal(".reveal", {
      delay: 200,
      distance: "50px",
      opacity: 0,
      easing: "ease-in",
      duration: 1000,
    });
  }, []);

  return (
    <div id="container-header">
      <nav className="logo">
        <img src={drapeau} alt="drapeau Malagasy" style={{ marginRight: "5px" }} />
        <span>TÃ©lepherique</span>
      </nav>
      <nav>
        <ul className={ulDisplay ? "ul-display" : ""}>
          {elements.map((element) => (
            <Link
              key={element}
              to={element.toLowerCase()} // Assurez-vous que ces ID correspondent aux sections de votre page
              smooth={true}
              duration={1000}
            >
              <li
                className="reveal"
                style={{ backgroundColor: element === elementSelectionner ? "#ffb703" : "" }}
                onClick={() => handleClickListe(element)}
              >
                {element}
              </li>
            </Link>
          ))}
        </ul>
      </nav>
      <nav id="search-bar" className={ulDisplay ? "searchResp" : ""}>
        <input type="search" placeholder="rechercher" />
        <i className="fas fa-search"></i>
      </nav>
      <nav className="switch-lang">
        <i className="fas fa-globe" style={{ marginRight: "2px" }} onClick={handleSwitchMgFr}></i>
        <span>{t("header.language")}</span>
        <div className="les-lang" style={visibility ? { display: "block" } : { display: "none" }}>
          <input
            type="button"
            value="FR"
            onClick={() => {
              handleSwitchLang("fr");
              setVisibility(false);
            }}
          />
          <input
            type="button"
            value="MG"
            onClick={() => {
              handleSwitchLang("mg");
              setVisibility(false);
            }}
          />
        </div>
      </nav>
      <nav className="les-btn">
        <input type="button" value={t("header.btnInscrire")} />
        <input type="button" value={t("header.btnConnecter")} />
      </nav>
      <nav>
        <i className="fas fa-bars" style={ulDisplay ? { display: "none" } : { display: "block" }} onClick={handleClickMenu}></i>
      </nav>
      <nav onClick={handleClose} className={ulDisplay ? "closeDisplay" : ""}>
        <i className="fas fa-close"></i>
      </nav>
    </div>
  );
}

export default Header;
