// src/components/Carousel.js
import React from 'react';
import Slider from 'react-slick';

const CarouselSlick = ({images}, {textes} ,{titres}) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="carousel">
      <Slider {...settings}>
        {images.map((image, index) => (
          <div key={index}>
            <img src={image} alt={`Slide ${index}`} className="carousel-image" />
          </div>
        ))}
       <Slider {...settings}>
        {textes.map(( texte, index) => (
          <div key={index}>
            <h1>{texte}</h1>
          </div>
        ))}
      </Slider>
      </Slider>
      <Slider {...settings}>
      {titres.map(( titre, index) => (
          <div key={index}>
            <p>{titre}</p>
          </div>
        ))}
         </Slider>
    </div>
  );
};

export default CarouselSlick;
